// ==UserScript==
// @name         globalrequest
// @version      1
// @description  none
// @run-at       document-start
// @author       rssaromeo
// @tag          unsecure
// @icon         data:image/png;base64,
// @grant        unsafeWindow
// @grant        GM_xmlhttpRequest
// @connect *
// @include *
// ==/UserScript==
function c() {
  if (!unsafeWindow.globalrequest) {
    unsafeWindow.globalrequest = async function globalrequest(data) {
      if (typeof data == "string") data = { url: data }
      var temp
      data.onload = (e) => (temp = e)
      GM_xmlhttpRequest(data)
      await waituntil(() => temp)
      temp.text = temp.responseText
      return temp
    }
  }
}
function waituntil(q, cb) {
  return new Promise((resolve) => {
    var int = setInterval(
      function (q, cb) {
        if (!!q()) {
          clearInterval(int)
          resolve()
        }
      },
      0,
      q,
      cb
    )
  })
}
c()
setInterval(c, 10000)
